function reviewsChangeSlide(e,t){var r,s,l,i;for(s=(r=document.querySelector(".reviews__slider")).querySelectorAll(".slider__controls-btn"),l=0;l<s.length;l++)s[l].classList.remove("slider__controls-btn--active");for(i=r.querySelectorAll(".reviews__slider-content"),l=0;l<i.length;l++)i[l].classList.remove("reviews__slider-content--active");document.getElementById(t).classList.add("reviews__slider-content--active"),e.currentTarget.classList.add("slider__controls-btn--active")}